from .config import load_config_schema
from .utils import NodeModuleSpec


class DockerfileLanguageServerNodeJS(NodeModuleSpec):
    node_module = key = "dockerfile-language-server-nodejs"
    script = ["lib", "server.js"]
    args = ["--stdio"]
    languages = ["dockerfile"]
    spec = dict(
        display_name=key,
        mime_types=["text/x-dockerfile"],
        urls=dict(
            home=f"https://github.com/rcjsuen/{key}",
            issues=f"https://github.com/rcjsuen/{key}/issues",
        ),
        install=dict(
            npm=f"npm install --save-dev {key}",
            yarn=f"yarn add --dev {key}",
            jlpm=f"jlpm add --dev {key}",
        ),
        config_schema=load_config_schema(key),
    )
