from .config import load_config_schema
from .utils import NodeModuleSpec


class SQLLanguageServer(NodeModuleSpec):
    """Supports mysql, postgres and sqlite3"""

    node_module = key = "sql-language-server"
    script = ["dist", "bin", "cli.js"]
    languages = [
        "sql",
    ]
    args = ["up", "--method", "stdio"]
    spec = dict(
        display_name=key,
        mime_types=[
            "application/sql",
            "text/sql",
            "text/x-sql",
            "text/x-mysql",
            "text/x-mariadb",
            "text/x-pgsql",
        ],
        urls=dict(
            home=f"https://github.com/joe-re/{key}",
            issues=f"https://github.com/joe-re/{key}/issues",
        ),
        install=dict(
            npm=f"npm install --save-dev {key}",
            yarn=f"yarn add --dev {key}",
            jlpm=f"jlpm add --dev {key}",
        ),
        config_schema=load_config_schema(key),
    )
