from .utils import NodeModuleSpec


class VSCodeCSSLanguageServer(NodeModuleSpec):
    node_module = key = "vscode-css-languageserver-bin"
    script = ["cssServerMain.js"]
    args = ["--stdio"]
    languages = ["css", "less", "scss"]
    spec = dict(
        display_name=key,
        mime_types=["text/x-scss", "text/css", "text/x-less"],
        urls=dict(
            home=f"https://github.com/vscode-langservers/{key}",
            issues=f"https://github.com/vscode-langservers/{key}/issues",
        ),
        install=dict(
            npm=f"npm install --save-dev {key}",
            yarn=f"yarn add --dev {key}",
            jlpm=f"jlpm add --dev {key}",
        ),
    )
