from .utils import NodeModuleSpec


class VSCodeJSONLanguageServer(NodeModuleSpec):
    node_module = key = "vscode-json-languageserver-bin"
    script = ["jsonServerMain.js"]
    args = ["--stdio"]
    languages = ["json"]
    spec = dict(
        display_name=key,
        mime_types=["application/json", "application/x-json", "application/ld+json"],
        urls=dict(
            home=f"https://github.com/vscode-langservers/{key}",
            issues=f"https://github.com/vscode-langservers/{key}/issues",
        ),
        install=dict(
            npm=f"npm install --save-dev {key}",
            yarn=f"yarn add --dev {key}",
            jlpm=f"jlpm add --dev {key}",
        ),
    )
