from .config import load_config_schema
from .utils import NodeModuleSpec


class YAMLLanguageServer(NodeModuleSpec):
    node_module = key = "yaml-language-server"
    script = ["bin", key]
    args = ["--stdio"]
    languages = ["yaml"]
    spec = dict(
        display_name=key,
        mime_types=["text/x-yaml", "text/yaml"],
        urls=dict(
            home=f"https://github.com/redhat-developer/{key}",
            issues=f"https://github.com/redhat-developer/{key}/issues",
        ),
        install=dict(
            npm=f"npm install --save-dev {key}",
            yarn=f"yarn add --dev {key}",
            jlpm=f"jlpm add --dev {key}",
        ),
        config_schema=load_config_schema(key),
    )
